import React, { Component } from 'react';
import { MDBContainer, MDBRow, MDBCol } from 'mdbreact';
import styled from 'styled-components';

const Wrapper = styled.div`
  height: 100vh;
  width: 100vw;

  .background{
    position: absolute;
    top: 0;
    left: 0;
    height: 100vh;
    width: 100vw;
    margin-top: -50vh;
    background-image: linear-gradient(10deg, #f4c44a, #f44336, #f44336, #f44336);
    transform:skewY(-5deg);
    z-index: -9999;
  }

  .foreground{
    padding-top: 60px;
  }

  .lobster{
    font-family: "Lobster Two", cursive;
  }

  .center {
    text-align: center;
  }

  .title{
    font-size: calc(3vw + 40px);
  }

  .subtitle{
    font-size: calc(0.5vw + 10px);
  }

`;

class LandingSection extends Component {
  render(){
    return(
      <Wrapper>
        <div className="background" />
        <MDBContainer className="foreground" fluid>
          <MDBRow>
            <MDBCol sm="6" className="center">
              <span className="title lobster text-white">Localateur</span>
              <p className="subtitle text-white">New Customers Discovering You</p>
              <div>
              <p className="description text-white">Increase your discoverability, target market, and quality of customer relations through the Localateur business app.
              We promote sharing of experiences between you and your customers for a more personalised and enjoyable experience.</p>
              </div>
            </MDBCol>
            <MDBCol sm="6">
              test
            </MDBCol>
          </MDBRow>
        </MDBContainer>
      </Wrapper>
    );
  }
}

export { LandingSection };
